INSERT INTO `intrinsic` VALUES (1,'Sau khi hạ 9 lính hoặc quái, Ahri sẽ được hồi máu. Khi tham gia hạ gục tướng, Ahri sẽ được hồi máu nhiều hơn.','HÚT HỒN','Ahri_i.mp4','AhriI.png'),(2,'Theo chu kỳ, đòn đánh kế tiếp của Aatrox gây thêm sát thương vật lý và hồi máu, dựa theo máu tối đa của mục tiêu.','ĐƯỜNG KIẾM TUYỆT DIỆT','Aatrox_i.mp4','AatroxI.png'),(3,'Dùng kỹ năng sát thương tướng địch tạo một vòng năng lượng quanh chúng. Rời khỏi vòng này cường hóa đòn đánh kế tiếp của Akali, giúp tăng tầm và sát thương.','DẤU ẤN SÁT THỦ','Akali_i.mp4','AkaliI.png'),(4,'Azir có thể triệu hồi Đĩa Mặt Trời từ tàn tích trụ.','DI SẢN CỦA SHURIMA','Azir_i.mp4','AzirI.png'),(5,'Cassiopeia được tăng Tốc độ Di chuyển mỗi cấp, nhưng không thể mua giày.','THẦN TỐC MÃNG XÀ','Cassiopeia_i.mp4','CassiopeiaI.png'),(6,'Khi Máu LeBlanc xuống dưới 40%, cô trở nên vô hình trong 1 giây và tạo một Ảo Ảnh không gây sát thương tồn tại tối đa 8 giây.','ẢO ẢNH','Leblanc_i.mp4','LeblancI.png'),(7,'Syndra thu thập các Mảnh Vụn Quyền Năng của cô khi lên cấp và gây sát thương lên kẻ địch, thứ sẽ giúp nâng cấp các kỹ năng của cô. Quả Cầu Bóng Tối: Syndra có thể giữ thêm một tích trữ kỹ năng Ý Lực: Gây thêm sát thương chuẩn. Quét Tan Kẻ Yếu: Tăng độ rộng kỹ năng và làm chậm tất cả mục tiêu Bùng Nổ Sức Mạnh: Kết liễu kẻ địch thấp máu','TỐI THƯỢNG','Syndra_i.mp4','SyndraI.png'),(8,'Tỉ lệ Chí mạng của Yasuo được gia tăng. Ngoài ra, Yasuo tích lá chắn mỗi khi di chuyển. Lá chắn kích hoạt khi anh chịu sát thương từ tướng hoặc quái.','ĐẠO CỦA LÃNG KHÁCH','Yasuo_i.mp4','YasuoI.png'),(9,'Đòn đánh cơ bản của Zed gây thêm Sát thương Phép lên kẻ địch thấp máu. Hiệu ứng này chỉ có thể xảy ra một lần mỗi vài giây lên tướng địch.','KHINH THƯỜNG KẺ YẾU','Zed_i.mp4','ZedI.png'),(10,'Sau khi tiêu diệt một đơn vị, Twisted Fate lắc xúc xắc \'may mắn\' của mình để nhận thêm từ 1 đến 6 vàng.','GIAN LẬN','TwistedFate_i.mp4','TwistedFateI.png'),(11,'Yone gây sát thương phép ở mỗi đòn Tấn công thứ hai. Bên cạnh đó, tỉ lệ chí mạng cũng được tăng.','ĐẠO CỦA KẺ SĂN','Yone_i.mp4','YoneI.png'),(12,'Đòn đánh thường của Xerath hồi Năng Lượng theo định kì. Mỗi khi Xerath kết liễu 1 đơn vị, hồi chiêu sẽ giảm đi.','ÁP XUNG NĂNG LƯỢNG','Xerath_i.mp4','XerathI.png'),(13,'Veigar là kẻ xấu xa tà ác vĩ đại nhất từng tồn tại trên Runeterra - và hắn ngày một vĩ đại hơn! Dùng kỹ năng trúng tướng hoặc tham gia hạ gục giúp Veigar tăng vĩnh viễn Sức mạnh Phép thuật.','SỨC MẠNH QUỶ QUYỆT','Veigar_i.mp4','VeigarI.png'),(14,'Đòn đánh của Sett đổi qua lại giữa tay trái và tay phải. Cú đấm tay phải mạnh và nhanh hơn một chút. Sett cũng được tăng tốc độ phục hồi máu dựa theo máu đã mất.','VUA ĐẤU TRƯỜNG','Sett_i.mp4','SettI.png'),(15,'Jinx nhận lượng lớn Tốc độ Di chuyển và Tốc độ Đánh khi tham gia hạ gục tướng, công trình, hoặc quái khủng.','HƯNG PHẤN!','Jinx_i.mp4','JinxI.png'),(16,'Ra lệnh di chuyển khi Kalista đang đánh thường hoặc dùng Đâm Xuyên sẽ khiến cô phóng về hướng đó khi tung đòn đánh.','PHONG THÁI QUÂN NHÂN','Kalista_i.mp4','KalistaI.png'),(17,'Sau khi Lee Sin sử dụng một kỹ năng, 2 đòn đánh kế tiếp tăng Tốc độ Đánh và hồi Nội Năng.','LOẠN ĐẢ','Leesin_i.mp4','LeeSinI.png'),(18,'Mỗi khi Lucian sử dụng kỹ năng, đòn đánh tiếp theo của anh sẽ bắn hai phát đạn. Khi nhận được hồi máu hay tạo lá chắn từ đồng minh, hoặc khi một tướng địch gần đó bị làm bất động, hai đòn đánh tiếp theo của Lucian gây thêm sát thương phép.','XẠ THỦ ÁNH SÁNG','Lucian_i.mp4','LucianI.png');

CREATE TABLE `w_skill` (
  `id` bigint NOT NULL,
